import{$ as n,a as t}from"./highlight-c6fe8555.js";import{$ as e}from"./codeblock-6ab1e2ba.js";var i=new window.nijor.component((async function(){return'<sectionn8f title="Custom Events" n-scope="PQwQXT5QgrPi46bw9VS">\n        We can create custom events in Nijor. What it means is that, just like events like <highlightn8f n-scope="PQwQXT5QgrPi46bw9VS">on:click</highlightn8f> exist in Nijor, we can create our own event like <highlightn8f n-scope="PQwQXT5QgrPi46bw9VS">on:myevent</highlightn8f>.\n        It\'s pretty easy to do so. <br n-scope="PQwQXT5QgrPi46bw9VS">\n        Let\'s consider a component <highlightn8f n-scope="PQwQXT5QgrPi46bw9VS">msg.nijor</highlightn8f> which uses a custom event <highlightn8f n-scope="PQwQXT5QgrPi46bw9VS">on:msg</highlightn8f>. Whenever a message is send from the <highlightn8f n-scope="PQwQXT5QgrPi46bw9VS">send.nijor</highlightn8f>, the sender\'s name and message gets printed in the paragraph tag. <br n-scope="PQwQXT5QgrPi46bw9VS"><br n-scope="PQwQXT5QgrPi46bw9VS">\n        \n        <coden8f file="src/page/msg.nijor" n-scope="PQwQXT5QgrPi46bw9VS">\n            &lt;template&gt;\n                &lt;div&gt;\n                    &lt;p id="msg" on:msg="PrintMsg($data)"&gt;\n                &lt;/div&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n            function PrintMsg(msgdata){\n                document.getElementById(\'msg\').innerHTML += `${msgdata.name} : ${msgdata.text} &lt;br&gt;`;\n            }\n            &lt;/script&gt;\n            &lt;script defer&gt;\n                // element.addEventListener doesn\'t work for custom events.\n                // document.getElementById(\'msg\').addEventListener(\'msg\',($data)=&gt;PrintMsg($data)); doesn\'t work for custom events.\n            &lt;/script&gt;\n        </coden8f>\n\n        <div style="display:none;" n-scope="PQwQXT5QgrPi46bw9VS"><coden8f n-scope="PQwQXT5QgrPi46bw9VS"></coden8f></div>\n\n        <coden8f file="src/page/send.nijor" n-scope="PQwQXT5QgrPi46bw9VS">\n            &lt;template&gt;\n                &lt;div&gt;\n                    &lt;input id="name"&gt;\n                    &lt;input id="message"&gt;\n                    &lt;input type="button" on:click="SendMsg()" value="Send"&gt;\n                &lt;/div&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n            function SendMsg(msgdata){\n                let name = document.getElementById(\'name\');\n                let message = document.getElementById(\'message\');\n                let data = {name:name,text:message};\n\n                // This fires the \'on:msg\' event across all the components that are currently in use.\n                // All the elements with on:msg events\' function\'s $data get replaced by {name:name,text:message}\n                window.nijor.emitEvent(\'msg\',data);\n            }\n            &lt;/script&gt;\n        </coden8f>\n    </sectionn8f>\n'}),(async function(){n.init("sectionn8f"),await n.run(),t.init("highlightn8f"),await t.run(),e.init("coden8f"),await e.run()}));export{i as default};
