import{$ as t,a as e}from"./highlight-f31d8d70.js";import n from"./codeblock-be73a27e.js";var s=new window.nijor.component((async function(){return'<sectionpcsv title="Custom Events" n-scope="z8wfoZdSj7">\n        We can create custom events in Nijor. What it means is that, just like events like <highlightpcsv n-scope="z8wfoZdSj7">on:click</highlightpcsv> exist in Nijor, we can create our own event like <highlightpcsv n-scope="z8wfoZdSj7">on:myevent</highlightpcsv>.\n        It\'s pretty easy to do so. <br n-scope="z8wfoZdSj7">\n        Let\'s consider a component <highlightpcsv n-scope="z8wfoZdSj7">msg.nijor</highlightpcsv> which uses a custom event <highlightpcsv n-scope="z8wfoZdSj7">on:msg</highlightpcsv>. Whenever a message is send from the <highlightpcsv n-scope="z8wfoZdSj7">send.nijor</highlightpcsv>, the sender\'s name and message gets printed in the paragraph tag. <br n-scope="z8wfoZdSj7"><br n-scope="z8wfoZdSj7">\n        \n        <codepcsv file="src/page/msg.nijor" n-scope="z8wfoZdSj7">\n            &lt;template&gt;\n                &lt;div&gt;\n                    &lt;p id="msg" on:msg="PrintMsg($data)"&gt;\n                &lt;/div&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n            function PrintMsg(msgdata){\n                document.getElementById(\'msg\').innerHTML += `${msgdata.name} : ${msgdata.text} &lt;br&gt;`;\n            }\n            &lt;/script&gt;\n            &lt;script defer&gt;\n                // element.addEventListener doesn\'t work for custom events.\n                // document.getElementById(\'msg\').addEventListener(\'msg\',($data)=&gt;PrintMsg($data)); doesn\'t work for custom events.\n            &lt;/script&gt;\n        </codepcsv>\n\n        <div style="display:none;" n-scope="z8wfoZdSj7"><codepcsv n-scope="z8wfoZdSj7"></codepcsv></div>\n\n        <codepcsv file="src/page/send.nijor" n-scope="z8wfoZdSj7">\n            &lt;template&gt;\n                &lt;div&gt;\n                    &lt;input id="name"&gt;\n                    &lt;input id="message"&gt;\n                    &lt;input type="button" on:click="SendMsg()" value="Send"&gt;\n                &lt;/div&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n            function SendMsg(msgdata){\n                let name = document.getElementById(\'name\');\n                let message = document.getElementById(\'message\');\n                let data = {name:name,text:message};\n\n                // This fires the \'on:msg\' event across all the components that are currently in use.\n                // All the elements with on:msg events\' function\'s $data get replaced by {name:name,text:message}\n                window.nijor.emitEvent(\'msg\',data);\n            }\n            &lt;/script&gt;\n        </codepcsv>\n    </sectionpcsv>\n'}),(async function(){t.init("sectionpcsv"),await t.run(),e.init("highlightpcsv"),await e.run(),n.init("codepcsv"),await n.run()}));export{s as default};
