import{$ as t,a as e}from"./highlight-87767d2c.js";import{$ as n}from"./codeblock-0a935fa0.js";var s=new window.nijor.component((async function(){return'<sectionxmw title="Custom Events" n-scope="srisstse">\n        We can create custom events in Nijor. What it means is that, just like events like <highlightxmw n-scope="srisstse">on:click</highlightxmw> exist in Nijor, we can create our own event like <highlightxmw n-scope="srisstse">on:myevent</highlightxmw>.\n        It\'s pretty easy to do so. <br n-scope="srisstse">\n        Let\'s consider a component <highlightxmw n-scope="srisstse">msg.nijor</highlightxmw> which uses a custom event <highlightxmw n-scope="srisstse">on:msg</highlightxmw>. Whenever a message is send from the <highlightxmw n-scope="srisstse">send.nijor</highlightxmw>, the sender\'s name and message gets printed in the paragraph tag. <br n-scope="srisstse"><br n-scope="srisstse">\n        \n        <codexmw file="src/page/msg.nijor" n-scope="srisstse">\n            &lt;template&gt;\n                &lt;div&gt;\n                    &lt;p id="msg" on:msg="PrintMsg($data)"&gt;\n                &lt;/div&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n            function PrintMsg(msgdata){\n                document.getElementById(\'msg\').innerHTML += `${msgdata.name} : ${msgdata.text} &lt;br&gt;`;\n            }\n            &lt;/script&gt;\n            &lt;script defer&gt;\n                // element.addEventListener doesn\'t work for custom events.\n                // document.getElementById(\'msg\').addEventListener(\'msg\',($data)=&gt;PrintMsg($data)); doesn\'t work for custom events.\n            &lt;/script&gt;\n        </codexmw>\n\n        <div style="display:none;" n-scope="srisstse"><codexmw n-scope="srisstse"></codexmw></div>\n\n        <codexmw file="src/page/send.nijor" n-scope="srisstse">\n            &lt;template&gt;\n                &lt;div&gt;\n                    &lt;input id="name"&gt;\n                    &lt;input id="message"&gt;\n                    &lt;input type="button" on:click="SendMsg()" value="Send"&gt;\n                &lt;/div&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n            function SendMsg(msgdata){\n                let name = document.getElementById(\'name\');\n                let message = document.getElementById(\'message\');\n                let data = {name:name,text:message};\n\n                // This fires the \'on:msg\' event across all the components that are currently in use.\n                // All the elements with on:msg events\' function\'s $data get replaced by {name:name,text:message}\n                window.nijor.emitEvent(\'msg\',data);\n            }\n            &lt;/script&gt;\n        </codexmw>\n    </sectionxmw>\n'}),(async function(){t.init("sectionxmw"),await t.run(),e.init("highlightxmw"),await e.run(),n.init("codexmw"),await n.run()}));export{s as default};
