import{$ as t,a as e}from"./highlight-a2f415e0.js";import n from"./codeblock-3a0e8dc7.js";var s=new window.nijor.component((async function(){return'<sectionhc title="Custom Events" n-scope="MEPCjg9447AEhojW6">\n        We can create custom events in Nijor. What it means is that, just like events like <highlighthc n-scope="MEPCjg9447AEhojW6">on:click</highlighthc> exist in Nijor, we can create our own event like <highlighthc n-scope="MEPCjg9447AEhojW6">on:myevent</highlighthc>.\n        It\'s pretty easy to do so. <br n-scope="MEPCjg9447AEhojW6">\n        Let\'s consider a component <highlighthc n-scope="MEPCjg9447AEhojW6">msg.nijor</highlighthc> which uses a custom event <highlighthc n-scope="MEPCjg9447AEhojW6">on:msg</highlighthc>. Whenever a message is send from the <highlighthc n-scope="MEPCjg9447AEhojW6">send.nijor</highlighthc>, the sender\'s name and message gets printed in the paragraph tag. <br n-scope="MEPCjg9447AEhojW6"><br n-scope="MEPCjg9447AEhojW6">\n        \n        <codehc file="src/page/msg.nijor" n-scope="MEPCjg9447AEhojW6">\n            &lt;template&gt;\n                &lt;div&gt;\n                    &lt;p id="msg" on:msg="PrintMsg($data)"&gt;\n                &lt;/div&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n            function PrintMsg(msgdata){\n                document.getElementById(\'msg\').innerHTML += `${msgdata.name} : ${msgdata.text} &lt;br&gt;`;\n            }\n            &lt;/script&gt;\n            &lt;script defer&gt;\n                // element.addEventListener doesn\'t work for custom events.\n                // document.getElementById(\'msg\').addEventListener(\'msg\',($data)=&gt;PrintMsg($data)); doesn\'t work for custom events.\n            &lt;/script&gt;\n        </codehc>\n\n        <div style="display:none;" n-scope="MEPCjg9447AEhojW6"><codehc n-scope="MEPCjg9447AEhojW6"></codehc></div>\n\n        <codehc file="src/page/send.nijor" n-scope="MEPCjg9447AEhojW6">\n            &lt;template&gt;\n                &lt;div&gt;\n                    &lt;input id="name"&gt;\n                    &lt;input id="message"&gt;\n                    &lt;input type="button" on:click="SendMsg()" value="Send"&gt;\n                &lt;/div&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n            function SendMsg(msgdata){\n                let name = document.getElementById(\'name\');\n                let message = document.getElementById(\'message\');\n                let data = {name:name,text:message};\n\n                // This fires the \'on:msg\' event across all the components that are currently in use.\n                // All the elements with on:msg events\' function\'s $data get replaced by {name:name,text:message}\n                window.nijor.emitEvent(\'msg\',data);\n            }\n            &lt;/script&gt;\n        </codehc>\n    </sectionhc>\n'}),(async function(){t.init("sectionhc"),await t.run(),e.init("highlighthc"),await e.run(),n.init("codehc"),await n.run()}));export{s as default};
