import{$ as e,a as t}from"./highlight-87767d2c.js";import{$ as o}from"./codeblock-0a935fa0.js";var r=new window.nijor.component((async function(){return'<sectionrpyeo title="Implementation of Dark-Mode" n-scope="ZdcdHRfTdYvR">\n        Dark-Mode is very popular these days. You can implement dark mode using <highlightrpyeo n-scope="ZdcdHRfTdYvR">CSS media queries</highlightrpyeo>. But, it has a little problem : dark mode will only get activated if your browser theme is set to dark. <br n-scope="ZdcdHRfTdYvR">\n        So, what if you want to use dark-mode without your browser\'s dark-mode activated ? or What if you want the user to have the freedom to toggle between dark, light and automatic(based on browser\'s theme) modes without them changing the theme of the browser ? <br n-scope="ZdcdHRfTdYvR">\n        You need to use Javascript for these use cases, and Nijor has got it covered !\n    </sectionrpyeo>\n    <sectionrpyeo title="How to implement Dark Mode ?" n-scope="ZdcdHRfTdYvR">\n        To implement dark-mode in your Nijor website :\n        <coderpyeo lang="js" file="App.js" n-scope="ZdcdHRfTdYvR">\n            import "nijor";\n            import "nijor/theme"; // Add this line of code to your App.js file\n        </coderpyeo>\n\n        You can write separate CSS code for dark mode inside a Nijor Component by writing the CSS code for dark mode inside another <highlightrpyeo n-scope="ZdcdHRfTdYvR">style</highlightrpyeo> tag with the <highlightrpyeo n-scope="ZdcdHRfTdYvR">dark</highlightrpyeo> attribute.\n        <coderpyeo n-scope="ZdcdHRfTdYvR">\n            &lt;template&gt;\n                &lt;div&gt;\n                    &lt;p&gt;Hello !&lt;/p&gt;\n                &lt;/div&gt;\n            &lt;/template&gt;\n            &lt;style&gt;\n                div{\n                    background-color : white;\n                    padding : 0.5rem;\n                    color : black;\n                }\n            &lt;/style&gt;\n            &lt;style dark&gt;\n                div{\n                    background-color : rgb(31, 31, 31);\n                    color : rgb(0, 153, 255);\n                }\n            &lt;/style&gt;\n        </coderpyeo>\n    </sectionrpyeo>\n    <sectionrpyeo title="Methods provided by nijor/theme" n-scope="ZdcdHRfTdYvR">\n        <highlightrpyeo n-scope="ZdcdHRfTdYvR">window.nijor.getTheme()</highlightrpyeo> : This method returns the value of current theme (light, dark, auto) of website. <br n-scope="ZdcdHRfTdYvR">\n        <highlightrpyeo n-scope="ZdcdHRfTdYvR">window.nijor.getColorScheme()</highlightrpyeo> : This method returns the value of current theme (light, dark, auto) of browser. <br n-scope="ZdcdHRfTdYvR">\n        <highlightrpyeo n-scope="ZdcdHRfTdYvR">window.nijor.setTheme(theme)</highlightrpyeo> : This method changes the theme of the website according to the value of parameter theme. Accepted values are "dark", "light", "auto"(browser\'s theme).\n    </sectionrpyeo>\n'}),(async function(){e.init("sectionrpyeo"),await e.run(),t.init("highlightrpyeo"),await t.run(),o.init("coderpyeo"),await o.run()}));export{r as default};
