import{$ as e,a as t}from"./highlight-c6fe8555.js";import{$ as o}from"./codeblock-6ab1e2ba.js";var n=new window.nijor.component((async function(){return'<sectionffdw title="Implementation of Dark-Mode" n-scope="4JUaRUdmRjwva9Bo6jr2">\n        Dark-Mode is very popular these days. You can implement dark mode using <highlightffdw n-scope="4JUaRUdmRjwva9Bo6jr2">CSS media queries</highlightffdw>. But, it has a little problem : dark mode will only get activated if your browser theme is set to dark. <br n-scope="4JUaRUdmRjwva9Bo6jr2">\n        So, what if you want to use dark-mode without your browser\'s dark-mode activated ? or What if you want the user to have the freedom to toggle between dark, light and automatic(based on browser\'s theme) modes without them changing the theme of the browser ? <br n-scope="4JUaRUdmRjwva9Bo6jr2">\n        You need to use Javascript for these use cases, and Nijor has got it covered !\n    </sectionffdw>\n    <sectionffdw title="How to implement Dark Mode ?" n-scope="4JUaRUdmRjwva9Bo6jr2">\n        To implement dark-mode in your Nijor website :\n        <codeffdw lang="js" file="App.js" n-scope="4JUaRUdmRjwva9Bo6jr2">\n            import "nijor";\n            import "nijor/theme"; // Add this line of code to your App.js file\n        </codeffdw>\n\n        You can write separate CSS code for dark mode inside a Nijor Component by writing the CSS code for dark mode inside another <highlightffdw n-scope="4JUaRUdmRjwva9Bo6jr2">style</highlightffdw> tag with the <highlightffdw n-scope="4JUaRUdmRjwva9Bo6jr2">dark</highlightffdw> attribute.\n        <codeffdw n-scope="4JUaRUdmRjwva9Bo6jr2">\n            &lt;template&gt;\n                &lt;div&gt;\n                    &lt;p&gt;Hello !&lt;/p&gt;\n                &lt;/div&gt;\n            &lt;/template&gt;\n            &lt;style&gt;\n                div{\n                    background-color : white;\n                    padding : 0.5rem;\n                    color : black;\n                }\n            &lt;/style&gt;\n            &lt;style dark&gt;\n                div{\n                    background-color : rgb(31, 31, 31);\n                    color : rgb(0, 153, 255);\n                }\n            &lt;/style&gt;\n        </codeffdw>\n    </sectionffdw>\n    <sectionffdw title="Methods provided by nijor/theme" n-scope="4JUaRUdmRjwva9Bo6jr2">\n        <highlightffdw n-scope="4JUaRUdmRjwva9Bo6jr2">window.nijor.getTheme()</highlightffdw> : This method returns the value of current theme (light, dark, auto) of website. <br n-scope="4JUaRUdmRjwva9Bo6jr2">\n        <highlightffdw n-scope="4JUaRUdmRjwva9Bo6jr2">window.nijor.getColorScheme()</highlightffdw> : This method returns the value of current theme (light, dark, auto) of browser. <br n-scope="4JUaRUdmRjwva9Bo6jr2">\n        <highlightffdw n-scope="4JUaRUdmRjwva9Bo6jr2">window.nijor.setTheme(theme)</highlightffdw> : This method changes the theme of the website according to the value of parameter theme. Accepted values are "dark", "light", "auto"(browser\'s theme).\n    </sectionffdw>\n'}),(async function(){e.init("sectionffdw"),await e.run(),t.init("highlightffdw"),await t.run(),o.init("codeffdw"),await o.run()}));export{n as default};
