import{$ as t,a as n}from"./highlight-a2f415e0.js";import e from"./codeblock-3a0e8dc7.js";var i=new window.nijor.component((async function(){return'<div style="display:none;" n-scope="P4HsYABADaTD62UBHz"><codefq n-scope="P4HsYABADaTD62UBHz"></codefq></div>\n    <sectionfq title="Functional Attributes" n-scope="P4HsYABADaTD62UBHz">\n        Functional Attributes are special attributes available in Nijor for normal html tags. They help us to do specific tasks with less code.\n    </sectionfq>\n    <sectionfq title="n:route" n-scope="P4HsYABADaTD62UBHz">\n        In order to navigate between Nijor routes we use the <highlightfq n-scope="P4HsYABADaTD62UBHz">n:route</highlightfq> functional attribute on the anchor tag only. If we use the <highlightfq n-scope="P4HsYABADaTD62UBHz">href</highlightfq> attribute instead of <highlightfq n-scope="P4HsYABADaTD62UBHz">n:route</highlightfq>, we\'ll still be able to navigate. But the page will reload and the navigation won\'t be that smooth. Use <highlightfq n-scope="P4HsYABADaTD62UBHz">href</highlightfq> attribute for linking to external website.\n        <codefq n-scope="P4HsYABADaTD62UBHz">\n            &lt;a n:route="/"&gt;Index Page&lt;/a&gt; &lt;!--Routing will be smooth, the page won\'t refresh--&gt;\n            &lt;a href="/"&gt;Index Page&lt;/a&gt; &lt;!--Routing won\'t be smooth, the page will refresh--&gt;\n        </codefq>\n    </sectionfq>\n    <sectionfq title="n:for" n-scope="P4HsYABADaTD62UBHz">\n        This functional attribute is used to perform loops inside a html tag.\n        <codefq n-scope="P4HsYABADaTD62UBHz">\n            &lt;template&gt;\n                &lt;p&gt;Following are the Functional Attributes in Nijor :&lt;p&gt;\n                &lt;ul n:for="let x of AllAttributes"&gt;\n                    &lt;li&gt;{x}&lt;/li&gt;\n                &lt;/ul&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n                let AllAttributes = [\'n:route\',\'n:for\',\'n:reload\',\'n:asynLoad\'];\n            &lt;/script&gt;\n        </codefq>\n    </sectionfq>\n    <sectionfq title="n:reload" n-scope="P4HsYABADaTD62UBHz">\n        This functional attribute is used with <highlightfq n-scope="P4HsYABADaTD62UBHz">n:for</highlightfq> or <highlightfq n-scope="P4HsYABADaTD62UBHz">n:asyncLoad</highlightfq> functional attributes. <br n-scope="P4HsYABADaTD62UBHz">\n        To use this functional attribute, add this attribute to your desired HTML element and assign a <highlightfq n-scope="P4HsYABADaTD62UBHz">label</highlightfq> to it, then use the <highlightfq n-scope="P4HsYABADaTD62UBHz">window.nijor.reload(label)</highlightfq> function.\n        <codefq n-scope="P4HsYABADaTD62UBHz">\n            &lt;template&gt;\n                &lt;input type="text" id="newTodo"&gt;\n                &lt;input type="button" on:click="AddItem()"&gt;\n                &lt;ul n:for="let task of Todos" n:reload="todolist"&gt;\n                    &lt;li&gt;{Todos}&lt;/li&gt;\n                &lt;/ul&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n                let Todos = [\'Read a book\', \'Learn Nijor\'];\n                function AddItem(){\n                    let item = document.getElementById(\'newTodo\').value;\n                    Todos.push(item);\n                    window.nijor.reload(\'todolist\');\n                }\n\n                /*\n                    Whenever a new item is added to \'todo\', \n                    the element(s) whith attribute n:reload="todolist" get reloaded \n                    and hence the data within them also gets updated. \n                */\n                \n            &lt;/script&gt;\n        </codefq>\n    </sectionfq>\n    <div style="display:none;" n-scope="P4HsYABADaTD62UBHz"><codefq n-scope="P4HsYABADaTD62UBHz"></codefq></div>\n    <sectionfq title="n:asyncLoad" n-scope="P4HsYABADaTD62UBHz">\n        This functional attribute is used to fetch data from server asynchronously to child elemenents inside a parent element. \n        <codefq n-scope="P4HsYABADaTD62UBHz">\n            &lt;template&gt;\n                &lt;div n:asyncLoad="let data = await getData()"&gt;\n                    &lt;span&gt;Name : {data.name}&lt;span&gt;\n                    &lt;span&gt;Email : {data.email}&lt;span&gt;\n                    &lt;span&gt;Phone no : {data.phoneno}&lt;span&gt;\n                &lt;/div&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n                async function getData(){\n                    // This function fetches data from server.\n                    let response = await fetch(\'url\', {\n                        method: \'GET\',\n                        credentials: \'include\'\n                    });\n                    return (response.json());\n                }\n            &lt;/script&gt;\n        </codefq>\n    </sectionfq>\n    <sectionfq title="n:bind" n-scope="P4HsYABADaTD62UBHz">\n        This functional attribute is used to bind the value of an input tag to a <highlightfq link="/docs#reactivity" n-scope="P4HsYABADaTD62UBHz">reactive variable</highlightfq>.\n        <codefq n-scope="P4HsYABADaTD62UBHz">\n            &lt;template&gt;\n                &lt;h1&gt;Hello {{name}}&lt;/h1&gt;\n                &lt;input type="text" n:bind="name"&gt;\n\n                &lt;!--Whenever the value of the input tag with the attribute n:bind changes, the value of {{name}} changes automatically.--&gt;\n            &lt;/template&gt;\n        </codefq>\n    </sectionfq>\n'}),(async function(){t.init("sectionfq"),await t.run(),n.init("highlightfq"),await n.run(),e.init("codefq"),await e.run()}));export{i as default};
