import{$ as e,a as i}from"./highlight-87767d2c.js";var t=new window.nijor.component((async function(){return'<div class="container" n-scope="EsaQqGNlmiGhM">\n        <sectionsu title="History of Nijor" n-scope="EsaQqGNlmiGhM">\n            Hi ! I\'m Debarshi Pathak (alias <highlightsu link="https://twitter.com/DebAxom" n-scope="EsaQqGNlmiGhM">DebAxom</highlightsu>), the creator of Nijor. <br n-scope="EsaQqGNlmiGhM">\n            I am the Assamese teenager who created Nijor in 2020 at the age of 14 (was about to turn 15). <br n-scope="EsaQqGNlmiGhM">\n            I created Nijor because I didn\'t want to learn React or Angular as I felt that learning those frameworks is like kinda learning a new language completely. So I decided to create my own framework, Nijor. <br n-scope="EsaQqGNlmiGhM">\n            The word <highlightsu n-scope="EsaQqGNlmiGhM">Nijor (নিজৰ)</highlightsu> means "My" or "Mine" in the Assamese language. I chose the word "Nijor" for my framework because I could create "my own" custom HTML tags with attributes. I\'ve been using NijorJS since 2020 and I decided to write the <highlightsu link="/docs#intro" n-scope="EsaQqGNlmiGhM">docs</highlightsu> in 2023.\n        </sectionsu>\n    </div>\n'}),(async function(){e.init("sectionsu"),await e.run(),i.init("highlightsu"),await i.run()}));export{t as default};
