import{$ as e,a as o}from"./highlight-a2f415e0.js";var n=new window.nijor.component((async function(){return'<sectionrfv title="What is Nijor ?" n-scope="uEKdiN7lNfCg8q1HC7f">\n        Nijor is a component-based framework that allows developers to build websites with ease by breaking down the code into many small components which can be reused throughout the codebase. Thus, Nijor makes developement faster in comparison to plain HTML, CSS and JS. <br n-scope="uEKdiN7lNfCg8q1HC7f">\n        Nijor compiles down to browser readable code with the help of the <highlightrfv n-scope="uEKdiN7lNfCg8q1HC7f">nijor compiler</highlightrfv>.\n    </sectionrfv>\n    <sectionrfv title="Feature Nijor Provides :" n-scope="uEKdiN7lNfCg8q1HC7f">\n        <ul style="list-style: none;" n-scope="uEKdiN7lNfCg8q1HC7f">\n            <li n-scope="uEKdiN7lNfCg8q1HC7f">Component Based</li>\n            <li n-scope="uEKdiN7lNfCg8q1HC7f">Frontend Routing</li>\n            <li n-scope="uEKdiN7lNfCg8q1HC7f">Custom Events</li>\n            <li n-scope="uEKdiN7lNfCg8q1HC7f">Easy to implement Dark-Mode</li>\n        </ul>\n    </sectionrfv>\n    <sectionrfv title="Prerequisites" n-scope="uEKdiN7lNfCg8q1HC7f">\n        You need to have some basic knowledge about HTML, CSS and Javascript. You need to have Node.js installed on your computer before using Nijor although you don\'t need to know anything about NodeJS.\n    </sectionrfv>\n'}),(async function(){e.init("sectionrfv"),await e.run(),o.init("highlightrfv"),await o.run()}));export{n as default};
