import{$ as e,a as i}from"./highlight-c6fe8555.js";var t=new window.nijor.component((async function(){return'<div class="container" n-scope="Rl5qLa0VX2">\n        <sectionig title="History of Nijor" n-scope="Rl5qLa0VX2">\n            Hi ! I\'m Debarshi Pathak (alias <highlightig link="https://twitter.com/DebAxom" n-scope="Rl5qLa0VX2">DebAxom</highlightig>), the creator of Nijor. <br n-scope="Rl5qLa0VX2">\n            I am the Assamese teenager who created Nijor in 2020 at the age of 14 (was about to turn 15). <br n-scope="Rl5qLa0VX2">\n            I created Nijor because I didn\'t want to learn React or Angular as I felt that learning those frameworks is like kinda learning a new language completely. So I decided to create my own framework, Nijor. <br n-scope="Rl5qLa0VX2">\n            The word <highlightig n-scope="Rl5qLa0VX2">Nijor (নিজৰ)</highlightig> means "My" or "Mine" in the Assamese language. I chose the word "Nijor" for my framework because I could create "my own" custom HTML tags with attributes. I\'ve been using NijorJS since 2020 and I decided to write the <highlightig link="/docs#intro" n-scope="Rl5qLa0VX2">docs</highlightig> in 2023.\n        </sectionig>\n    </div>\n'}),(async function(){e.init("sectionig"),await e.run(),i.init("highlightig"),await i.run()}));export{t as default};
