import{$ as i,a as n}from"./highlight-a2f415e0.js";import e from"./codeblock-3a0e8dc7.js";var o=new window.nijor.component((async function(){return'<div style="display:none;" n-scope="6WEdATny6Oi9246XiGS"><codewqf9q n-scope="6WEdATny6Oi9246XiGS"></codewqf9q></div>\n    <sectionwqf9q title="Nijor-CLI" n-scope="6WEdATny6Oi9246XiGS"></sectionwqf9q>\n    <sectionwqf9q title="Intorduction" n-scope="6WEdATny6Oi9246XiGS">\n        The <highlightwqf9q n-scope="6WEdATny6Oi9246XiGS">nijor-cli</highlightwqf9q> is a CLI tool which allows to work in Nijor. <br n-scope="6WEdATny6Oi9246XiGS">\n    </sectionwqf9q>\n    <sectionwqf9q title="nijor create" n-scope="6WEdATny6Oi9246XiGS">\n        The <highlightwqf9q n-scope="6WEdATny6Oi9246XiGS">nijor create</highlightwqf9q> command creates a new folder and generates some boilerplate code.\n        To create a new Nijor project, type the following commands. (Write your project\'s name instead of {Appname} )\n        <codewqf9q lang="bash" n-scope="6WEdATny6Oi9246XiGS">nijor create {AppName}</codewqf9q>\n        <codewqf9q lang="bash" n-scope="6WEdATny6Oi9246XiGS">cd {AppName}</codewqf9q>\n    </sectionwqf9q>\n    <sectionwqf9q title="nijor serve" n-scope="6WEdATny6Oi9246XiGS">\n        The <highlightwqf9q n-scope="6WEdATny6Oi9246XiGS">nijor serve</highlightwqf9q> command runs a development server for the Nijor Project.\n        <codewqf9q lang="bash" n-scope="6WEdATny6Oi9246XiGS">nijor serve</codewqf9q>\n    </sectionwqf9q>\n    <sectionwqf9q title="nijor compile" n-scope="6WEdATny6Oi9246XiGS">\n        The <highlightwqf9q n-scope="6WEdATny6Oi9246XiGS">nijor compile</highlightwqf9q> command compiles the all the <highlightwqf9q n-scope="6WEdATny6Oi9246XiGS">*.nijor</highlightwqf9q> files to browser readable JS and CSS code.\n        <codewqf9q lang="bash" n-scope="6WEdATny6Oi9246XiGS">nijor compile</codewqf9q>\n        To watch for changes, type\n        <codewqf9q lang="bash" n-scope="6WEdATny6Oi9246XiGS">nijor compile -w</codewqf9q>\n    </sectionwqf9q>\n    <sectionwqf9q title="nijor build" n-scope="6WEdATny6Oi9246XiGS">\n        The <highlightwqf9q n-scope="6WEdATny6Oi9246XiGS">nijor build</highlightwqf9q> command is very similar to the<highlightwqf9q n-scope="6WEdATny6Oi9246XiGS">nijor compile</highlightwqf9q> command. The only differnce between the 2 is that <highlightwqf9q n-scope="6WEdATny6Oi9246XiGS">nijor build</highlightwqf9q> minifies the JS code.\n        <codewqf9q lang="bash" n-scope="6WEdATny6Oi9246XiGS">nijor build</codewqf9q>\n    </sectionwqf9q>\n'}),(async function(){i.init("sectionwqf9q"),await i.run(),n.init("highlightwqf9q"),await n.run(),e.init("codewqf9q"),await e.run()}));export{o as default};
