import{$ as e,a as n}from"./highlight-c6fe8555.js";import{$ as o}from"./codeblock-6ab1e2ba.js";var i=new window.nijor.component((async function(){return'<div style="display:none;" n-scope="jHyeMtIlY49mPggN"><code0u8 n-scope="jHyeMtIlY49mPggN"></code0u8></div>\n    <section0u8 title="Nijor-CLI" n-scope="jHyeMtIlY49mPggN"></section0u8>\n    <section0u8 title="Intorduction" n-scope="jHyeMtIlY49mPggN">\n        The <highlight0u8 n-scope="jHyeMtIlY49mPggN">nijor-cli</highlight0u8> is a CLI tool which allows to work in Nijor. <br n-scope="jHyeMtIlY49mPggN">\n    </section0u8>\n    <section0u8 title="nijor create" n-scope="jHyeMtIlY49mPggN">\n        The <highlight0u8 n-scope="jHyeMtIlY49mPggN">nijor create</highlight0u8> command creates a new folder and generates some boilerplate code.\n        To create a new Nijor project, type the following commands. (Write your project\'s name instead of {Appname} )\n        <code0u8 lang="bash" n-scope="jHyeMtIlY49mPggN">nijor create {AppName}</code0u8>\n        <code0u8 lang="bash" n-scope="jHyeMtIlY49mPggN">cd {AppName}</code0u8>\n    </section0u8>\n    <section0u8 title="nijor serve" n-scope="jHyeMtIlY49mPggN">\n        The <highlight0u8 n-scope="jHyeMtIlY49mPggN">nijor serve</highlight0u8> command runs a development server for the Nijor Project.\n        <code0u8 lang="bash" n-scope="jHyeMtIlY49mPggN">nijor serve</code0u8>\n    </section0u8>\n    <section0u8 title="nijor compile" n-scope="jHyeMtIlY49mPggN">\n        The <highlight0u8 n-scope="jHyeMtIlY49mPggN">nijor compile</highlight0u8> command compiles the all the <highlight0u8 n-scope="jHyeMtIlY49mPggN">*.nijor</highlight0u8> files to browser readable JS and CSS code.\n        <code0u8 lang="bash" n-scope="jHyeMtIlY49mPggN">nijor compile</code0u8>\n        To watch for changes, type\n        <code0u8 lang="bash" n-scope="jHyeMtIlY49mPggN">nijor compile -w</code0u8>\n    </section0u8>\n    <section0u8 title="nijor build" n-scope="jHyeMtIlY49mPggN">\n        The <highlight0u8 n-scope="jHyeMtIlY49mPggN">nijor build</highlight0u8> command is very similar to the<highlight0u8 n-scope="jHyeMtIlY49mPggN">nijor compile</highlight0u8> command. The only differnce between the 2 is that <highlight0u8 n-scope="jHyeMtIlY49mPggN">nijor build</highlight0u8> minifies the JS code.\n        <code0u8 lang="bash" n-scope="jHyeMtIlY49mPggN">nijor build</code0u8>\n    </section0u8>\n'}),(async function(){e.init("section0u8"),await e.run(),n.init("highlight0u8"),await n.run(),o.init("code0u8"),await o.run()}));export{i as default};
