import{$ as o,a as e}from"./highlight-87767d2c.js";import{$ as t}from"./codeblock-0a935fa0.js";var n=new window.nijor.component((async function(){return'<div style="display:none;" n-scope="A63HoPMMM5M"><codeapt n-scope="A63HoPMMM5M"></codeapt></div>\n    <sectionapt title="Nijor-CLI" n-scope="A63HoPMMM5M"></sectionapt>\n    <sectionapt title="Intorduction" n-scope="A63HoPMMM5M">\n        The <highlightapt n-scope="A63HoPMMM5M">nijor-cli</highlightapt> is a CLI tool which allows to work in Nijor. <br n-scope="A63HoPMMM5M">\n    </sectionapt>\n    <sectionapt title="nijor create" n-scope="A63HoPMMM5M">\n        The <highlightapt n-scope="A63HoPMMM5M">nijor create</highlightapt> command creates a new folder and generates some boilerplate code.\n        To create a new Nijor project, type the following commands. (Write your project\'s name instead of {Appname} )\n        <codeapt lang="bash" n-scope="A63HoPMMM5M">nijor create {AppName}</codeapt>\n        <codeapt lang="bash" n-scope="A63HoPMMM5M">cd {AppName}</codeapt>\n    </sectionapt>\n    <sectionapt title="nijor serve" n-scope="A63HoPMMM5M">\n        The <highlightapt n-scope="A63HoPMMM5M">nijor serve</highlightapt> command runs a development server for the Nijor Project.\n        <codeapt lang="bash" n-scope="A63HoPMMM5M">nijor serve</codeapt>\n    </sectionapt>\n    <sectionapt title="nijor compile" n-scope="A63HoPMMM5M">\n        The <highlightapt n-scope="A63HoPMMM5M">nijor compile</highlightapt> command compiles the all the <highlightapt n-scope="A63HoPMMM5M">*.nijor</highlightapt> files to browser readable JS and CSS code.\n        <codeapt lang="bash" n-scope="A63HoPMMM5M">nijor compile</codeapt>\n        To watch for changes, type\n        <codeapt lang="bash" n-scope="A63HoPMMM5M">nijor compile -w</codeapt>\n    </sectionapt>\n    <sectionapt title="nijor build" n-scope="A63HoPMMM5M">\n        The <highlightapt n-scope="A63HoPMMM5M">nijor build</highlightapt> command is very similar to the<highlightapt n-scope="A63HoPMMM5M">nijor compile</highlightapt> command. The only differnce between the 2 is that <highlightapt n-scope="A63HoPMMM5M">nijor build</highlightapt> minifies the JS code.\n        <codeapt lang="bash" n-scope="A63HoPMMM5M">nijor build</codeapt>\n    </sectionapt>\n'}),(async function(){o.init("sectionapt"),await o.run(),e.init("highlightapt"),await e.run(),t.init("codeapt"),await t.run()}));export{n as default};
