import{$ as n,a as e}from"./highlight-f31d8d70.js";import o from"./codeblock-be73a27e.js";var h=new window.nijor.component((async function(){return'<div style="display:none;" n-scope="flUqznr9j8mDEqxw"><codehxgjq n-scope="flUqznr9j8mDEqxw"></codehxgjq></div>\n    <sectionhxgjq title="Nijor-CLI" n-scope="flUqznr9j8mDEqxw"></sectionhxgjq>\n    <sectionhxgjq title="Intorduction" n-scope="flUqznr9j8mDEqxw">\n        The <highlighthxgjq n-scope="flUqznr9j8mDEqxw">nijor-cli</highlighthxgjq> is a CLI tool which allows to work in Nijor. <br n-scope="flUqznr9j8mDEqxw">\n    </sectionhxgjq>\n    <sectionhxgjq title="nijor create" n-scope="flUqznr9j8mDEqxw">\n        The <highlighthxgjq n-scope="flUqznr9j8mDEqxw">nijor create</highlighthxgjq> command creates a new folder and generates some boilerplate code.\n        To create a new Nijor project, type the following commands. (Write your project\'s name instead of {Appname} )\n        <codehxgjq lang="bash" n-scope="flUqznr9j8mDEqxw">nijor create {AppName}</codehxgjq>\n        <codehxgjq lang="bash" n-scope="flUqznr9j8mDEqxw">cd {AppName}</codehxgjq>\n    </sectionhxgjq>\n    <sectionhxgjq title="nijor serve" n-scope="flUqznr9j8mDEqxw">\n        The <highlighthxgjq n-scope="flUqznr9j8mDEqxw">nijor serve</highlighthxgjq> command runs a development server for the Nijor Project.\n        <codehxgjq lang="bash" n-scope="flUqznr9j8mDEqxw">nijor serve</codehxgjq>\n    </sectionhxgjq>\n    <sectionhxgjq title="nijor compile" n-scope="flUqznr9j8mDEqxw">\n        The <highlighthxgjq n-scope="flUqznr9j8mDEqxw">nijor compile</highlighthxgjq> command compiles the all the <highlighthxgjq n-scope="flUqznr9j8mDEqxw">*.nijor</highlighthxgjq> files to browser readable JS and CSS code.\n        <codehxgjq lang="bash" n-scope="flUqznr9j8mDEqxw">nijor compile</codehxgjq>\n        To watch for changes, type\n        <codehxgjq lang="bash" n-scope="flUqznr9j8mDEqxw">nijor compile -w</codehxgjq>\n    </sectionhxgjq>\n    <sectionhxgjq title="nijor build" n-scope="flUqznr9j8mDEqxw">\n        The <highlighthxgjq n-scope="flUqznr9j8mDEqxw">nijor build</highlighthxgjq> command is very similar to the<highlighthxgjq n-scope="flUqznr9j8mDEqxw">nijor compile</highlighthxgjq> command. The only differnce between the 2 is that <highlighthxgjq n-scope="flUqznr9j8mDEqxw">nijor build</highlighthxgjq> minifies the JS code.\n        <codehxgjq lang="bash" n-scope="flUqznr9j8mDEqxw">nijor build</codehxgjq>\n    </sectionhxgjq>\n'}),(async function(){n.init("sectionhxgjq"),await n.run(),e.init("highlighthxgjq"),await e.run(),o.init("codehxgjq"),await o.run()}));export{h as default};
