import{$ as e,a as t}from"./highlight-a2f415e0.js";import a from"./codeblock-3a0e8dc7.js";var i=new window.nijor.component((async function(){return'<div style="display:none;" n-scope="9fVD9zG0CXauuL"><codepbuc n-scope="9fVD9zG0CXauuL"></codepbuc></div>\n    <sectionpbuc title="Reactivity" n-scope="9fVD9zG0CXauuL">\n        In Nijor, we can define <highlightpbuc n-scope="9fVD9zG0CXauuL">reactive variables</highlightpbuc>.\n        Reactive variables are defined as properties of the window.nijor.reactiveVars object. You can use the syntax, <highlightpbuc n-scope="9fVD9zG0CXauuL">window.nijor.reactiveVars[\'varname\']</highlightpbuc> to create a reactive variable. To access the value of a reactive variable inside the template tag, use the <highlightpbuc n-scope="9fVD9zG0CXauuL">{{varname}}</highlightpbuc> syntax.\n        <codepbuc n-scope="9fVD9zG0CXauuL">\n            &lt;template&gt;\n                &lt;!--To fetch the value of a reactive variable within the Nijor template, use {{variable}} --&gt;\n                &lt;!--Whenever the value of window.nijor.reactiveVars[\'count\'] changes, the value of {{count}} will automatically change--&gt;\n                &lt;button on:click="UpdateCount()"&gt;{{count}}&lt;/button&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n                window.nijor.reactiveVars[\'count\'] = 1; \n                // window.nijor.reactiveVars is a special object which contains a key-value pair of reactive variables and their values.\n                // Changes in values of reactive variables get reflected on the DOM only.\n                function UpdateCount(){\n                    window.nijor.reactiveVars[\'count\']+=1;\n                }\n            &lt;/script&gt;\n        </codepbuc>\n    </sectionpbuc>\n'}),(async function(){e.init("sectionpbuc"),await e.run(),t.init("highlightpbuc"),await t.run(),a.init("codepbuc"),await a.run()}));export{i as default};
