import{$ as e,a as t}from"./highlight-c6fe8555.js";import{$ as i}from"./codeblock-6ab1e2ba.js";var n=new window.nijor.component((async function(){return'<div style="display:none;" n-scope="ZVmx0ZBfAZ7BJwWJ2dGZ"><codezuqh n-scope="ZVmx0ZBfAZ7BJwWJ2dGZ"></codezuqh></div>\n    <sectionzuqh title="Reactivity" n-scope="ZVmx0ZBfAZ7BJwWJ2dGZ">\n        In Nijor, we can define <highlightzuqh n-scope="ZVmx0ZBfAZ7BJwWJ2dGZ">reactive variables</highlightzuqh>.\n        Reactive variables are defined as properties of the window.nijor.reactiveVars object. You can use the syntax, <highlightzuqh n-scope="ZVmx0ZBfAZ7BJwWJ2dGZ">window.nijor.reactiveVars[\'varname\']</highlightzuqh> to create a reactive variable. To access the value of a reactive variable inside the template tag, use the <highlightzuqh n-scope="ZVmx0ZBfAZ7BJwWJ2dGZ">{{varname}}</highlightzuqh> syntax.\n        <codezuqh n-scope="ZVmx0ZBfAZ7BJwWJ2dGZ">\n            &lt;template&gt;\n                &lt;!--To fetch the value of a reactive variable within the Nijor template, use {{variable}} --&gt;\n                &lt;!--Whenever the value of window.nijor.reactiveVars[\'count\'] changes, the value of {{count}} will automatically change--&gt;\n                &lt;button on:click="UpdateCount()"&gt;{{count}}&lt;/button&gt;\n            &lt;/template&gt;\n            &lt;script&gt;\n                window.nijor.reactiveVars[\'count\'] = 1; \n                // window.nijor.reactiveVars is a special object which contains a key-value pair of reactive variables and their values.\n                // Changes in values of reactive variables get reflected on the DOM only.\n                function UpdateCount(){\n                    window.nijor.reactiveVars[\'count\']+=1;\n                }\n            &lt;/script&gt;\n        </codezuqh>\n    </sectionzuqh>\n'}),(async function(){e.init("sectionzuqh"),await e.run(),t.init("highlightzuqh"),await t.run(),i.init("codezuqh"),await i.run()}));export{n as default};
