import{$ as i,a as t}from"./highlight-87767d2c.js";import{$ as e}from"./codeblock-0a935fa0.js";var o=new window.nijor.component((async function(){return'<div style="display: none;" n-scope="2Hqz6OQ6cfQt8kLwlGs"><codepy4k n-scope="2Hqz6OQ6cfQt8kLwlGs"></codepy4k></div>\n    <sectionpy4k title="Structure of a Nijor project" n-scope="2Hqz6OQ6cfQt8kLwlGs">\n        When you use the <highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">nijor create {appname}</highlightpy4k> command, it creates a new directory named after your project. This page is intended to help you understand the use of each of these directories and files.\n    </sectionpy4k>\n    <sectionpy4k title="app directory" n-scope="2Hqz6OQ6cfQt8kLwlGs">\n        The <highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">app</highlightpy4k> directory contains the <highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">index.html</highlightpy4k> file for our website. It also contains another directory called <highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">assets</highlightpy4k> which stores our css file and it also contains another directory called <highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">modules</highlightpy4k> which contains the js files produced by the compiler.\n    </sectionpy4k>\n    <sectionpy4k title="src directory" n-scope="2Hqz6OQ6cfQt8kLwlGs">\n        The <highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">src</highlightpy4k> directory is our main working directory. We generally don\'t need to touch the <highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">app</highlightpy4k> directory.\n    </sectionpy4k>\n    <sectionpy4k title="src/components directory" n-scope="2Hqz6OQ6cfQt8kLwlGs">\n        This directory stores all the reusable Nijor components.\n    </sectionpy4k>\n    <sectionpy4k title="src/pages directory" n-scope="2Hqz6OQ6cfQt8kLwlGs">\n        This directory stores all the Nijor components which are to be used as <highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">pages</highlightpy4k> during routing. Pages are just normal Nijor components which are used for routing, they behave normally as other Nijor components.\n    </sectionpy4k>\n    <sectionpy4k title="src/styles directory" n-scope="2Hqz6OQ6cfQt8kLwlGs">\n        This directory stores our stylesheets.\n    </sectionpy4k>\n    <sectionpy4k title="src/images directory" n-scope="2Hqz6OQ6cfQt8kLwlGs">\n        This directory stores our images which can be imported into a Nijor component just like a Javascript file using ES6 imports.\n    </sectionpy4k>\n    <sectionpy4k title="src/App.js file" n-scope="2Hqz6OQ6cfQt8kLwlGs">\n        This file is the main entrypoint of our Nijor project.\n    </sectionpy4k>\n    <sectionpy4k title="src/App.nijor file" n-scope="2Hqz6OQ6cfQt8kLwlGs">\n        This file is like the index.html for Nijor. We still need to use the index.html file sometimes though. The template tag of App.nijor is like the body of the index.html file.\n    </sectionpy4k>\n    <sectionpy4k title="Nijor Configuration File" n-scope="2Hqz6OQ6cfQt8kLwlGs">\n        The <highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">nijor.config.json</highlightpy4k> is a file which contains certain configurations for a Nijor project.<br n-scope="2Hqz6OQ6cfQt8kLwlGs">\n        If you try to run the <highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">nijor build</highlightpy4k>,<highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">nijor compile</highlightpy4k>,<highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">nijor serve</highlightpy4k> commands inside a Nijor project without this file, then the <highlightpy4k n-scope="2Hqz6OQ6cfQt8kLwlGs">nijor-compiler</highlightpy4k> won\'t work and will throw errors.\n    </sectionpy4k>\n'}),(async function(){i.init("sectionpy4k"),await i.run(),t.init("highlightpy4k"),await t.run(),e.init("codepy4k"),await e.run()}));export{o as default};
