import{$ as i,a as e}from"./highlight-a2f415e0.js";import t from"./codeblock-3a0e8dc7.js";var h=new window.nijor.component((async function(){return'<div style="display: none;" n-scope="UrxfqS"><codeadmah n-scope="UrxfqS"></codeadmah></div>\n    <sectionadmah title="Structure of a Nijor project" n-scope="UrxfqS">\n        When you use the <highlightadmah n-scope="UrxfqS">nijor create {appname}</highlightadmah> command, it creates a new directory named after your project. This page is intended to help you understand the use of each of these directories and files.\n    </sectionadmah>\n    <sectionadmah title="app directory" n-scope="UrxfqS">\n        The <highlightadmah n-scope="UrxfqS">app</highlightadmah> directory contains the <highlightadmah n-scope="UrxfqS">index.html</highlightadmah> file for our website. It also contains another directory called <highlightadmah n-scope="UrxfqS">assets</highlightadmah> which stores our css file and it also contains another directory called <highlightadmah n-scope="UrxfqS">modules</highlightadmah> which contains the js files produced by the compiler.\n    </sectionadmah>\n    <sectionadmah title="src directory" n-scope="UrxfqS">\n        The <highlightadmah n-scope="UrxfqS">src</highlightadmah> directory is our main working directory. We generally don\'t need to touch the <highlightadmah n-scope="UrxfqS">app</highlightadmah> directory.\n    </sectionadmah>\n    <sectionadmah title="src/components directory" n-scope="UrxfqS">\n        This directory stores all the reusable Nijor components.\n    </sectionadmah>\n    <sectionadmah title="src/pages directory" n-scope="UrxfqS">\n        This directory stores all the Nijor components which are to be used as <highlightadmah n-scope="UrxfqS">pages</highlightadmah> during routing. Pages are just normal Nijor components which are used for routing, they behave normally as other Nijor components.\n    </sectionadmah>\n    <sectionadmah title="src/styles directory" n-scope="UrxfqS">\n        This directory stores our global stylesheets.\n    </sectionadmah>\n    <sectionadmah title="src/images directory" n-scope="UrxfqS">\n        This directory stores our images which can be imported into a Nijor component just like a Javascript file using ES6 imports.\n    </sectionadmah>\n    <sectionadmah title="src/App.js file" n-scope="UrxfqS">\n        This file is the main entrypoint of our Nijor project.\n    </sectionadmah>\n    <sectionadmah title="src/App.nijor file" n-scope="UrxfqS">\n        This file is like the index.html for Nijor. We still need to use the index.html file sometimes though. The template tag of App.nijor is like the body of the index.html file.\n    </sectionadmah>\n    <sectionadmah title="Nijor Configuration File" n-scope="UrxfqS">\n        The <highlightadmah n-scope="UrxfqS">nijor.config.json</highlightadmah> is a file which contains certain configurations for a Nijor project.<br n-scope="UrxfqS">\n        If you try to run the <highlightadmah n-scope="UrxfqS">nijor build</highlightadmah>,<highlightadmah n-scope="UrxfqS">nijor compile</highlightadmah>,<highlightadmah n-scope="UrxfqS">nijor serve</highlightadmah> commands inside a Nijor project without this file, then the <highlightadmah n-scope="UrxfqS">nijor-compiler</highlightadmah> won\'t work and will throw errors.\n    </sectionadmah>\n'}),(async function(){i.init("sectionadmah"),await i.run(),e.init("highlightadmah"),await e.run(),t.init("codeadmah"),await t.run()}));export{h as default};
